const myModule=(()=>{const e=document.querySelector("#PerimetroCuadrado"),t=document.querySelector("#AreaCuadrado"),n=document.querySelector("#PerimetroTriangulo"),r=document.querySelector("#AreaTriangulo"),l=document.querySelector("#DiametroCirculo"),u=document.querySelector("#PerimetroCirculo"),d=document.querySelector("#AreaCirculo"),o=document.getElementById("result");e.addEventListener("click",()=>{let e=document.getElementById("inputSide");const t=`El perímetro de Cuadrado es: ${(e=>4*e)(Number(e.value))} cm`;o.innerText=t}),t.addEventListener("click",()=>{let e=document.getElementById("inputSide");const t=`El Area del Cuadrado es: ${(e=>e*e)(Number(e.value))} cm`;o.innerText=t}),r.addEventListener("click",()=>{let e=document.getElementById("inputBase"),t=Number(e.value),n=document.getElementById("inputAltura");const r=`El Area del Triangulo es : ${((e,t)=>e*t/2)(t,Number(n.value))} cm `;o.innerText=r}),n.addEventListener("click",()=>{let e=document.getElementById("triangulolado1"),t=Number(e.value),n=document.getElementById("triangulolado2"),r=Number(n.value),l=document.getElementById("triangulolado3");const u=`Èl valor del perimetro es : ${((e,t,n)=>e+t+n)(t,r,Number(l.value))} cm`;o.innerText=u});const c=e=>2*e;l.addEventListener("click",()=>{let e=document.getElementById("inputRadio"),t=Number(e.value);const n=`El valor del Diametro es : ${c(t)}`;o.innerText=n}),u.addEventListener("click",()=>{let e=document.getElementById("inputRadio");const t=`El valor del Perimetro del Circulo es : ${(e=>c(e)*Math.PI)(Number(e.value))}`;o.innerText=t}),d.addEventListener("click",()=>{let e=document.getElementById("inputRadio");const t=`El valor del Area del Circulo es : ${(e=>e*e*Math.PI)(Number(e.value))}`;o.innerText=t})})();
